@register.simple_tag
def icon(name, classes="w-5 h-5", **kwargs):
    """Render a Lucide icon as inline SVG.

    Usage:
        {% icon "check" %}
        {% icon "check" "w-4 h-4 text-green-500" %}
        {% icon "check" classes="w-6 h-6" %}
    """
    path = ICON_PATHS.get(name, ICON_PATHS.get("x"))  # fallback to x icon

    extra_attrs = " ".join(f'{k.replace("_", "-")}="{v}"' for k, v in kwargs.items())
    if extra_attrs:
        extra_attrs = " " + extra_attrs

    svg = f'''<svg xmlns="http://www.w3.org/2000/svg" class="{classes}" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"{extra_attrs}>{path}</svg>'''

    return mark_safe(svg)
